import "./navbar.scss";
import React, { useState } from "react";
import FilterButtons from "../filterButtons/FilterButtons";
import { Link } from "react-router-dom";
import { currenciesQuery } from "../../qraphQL/queries";
import { useQuery } from "@apollo/client";
const Navbar = () => {
  const { data, loading } = useQuery(currenciesQuery);
  const [currencyValue, setCurrencyValue] = useState(
    !loading && data.currencies[0].symbol
  );
  return (
    <div className="navbar-container">
      <div className="first">
        <FilterButtons />
      </div>
      <div className="last">
        <div className="currenciesChange">
          <button>{currencyValue} </button>
          {!loading &&
            data.currencies.map(({ symbol, label }) => (
              <span key={symbol}>
                {symbol} {label}
              </span>
            ))}
        </div>
        <Link to="/cart">
          <i className="fal fa-shopping-cart"></i>
        </Link>
      </div>
    </div>
  );
};
export default Navbar;
