import {
  ApolloClient,
  InMemoryCache,
  gql,
} from "@apollo/client";

export  const client = new ApolloClient({
  uri: "http://localhost:4000/",
  cache: new InMemoryCache(),
});
  
export const categoriesQuery = gql`
  query { 
	categori {
    name
  }
} `;
