import React, { useEffect, useState } from "react";
import { Routes, Route, BrowserRouter } from "react-router-dom";
import ProductsListPage from "./pages/PLP/ProductsListPage";
import ProductDetailsPage from "./pages/PDP/ProductDetailsPage";
import CartPage from "./pages/CART/CartPage";
import ContextHook from "./components/ContextHook";
import Navbar from "./components/navbar/Navbar";
// import { categoriesQuery } from "./qraphQL/ApolloClient";
import { useQuery } from "@apollo/client";
import { currenciesQuery } from "./qraphQL/ApolloClient";
const App = () => {
  const currenciesQuery = useQuery(currenciesQuery);

  return (
    <BrowserRouter>
      <ContextHook.Provider
        value={{
          curreniesQuery,
        }}
      >
        <div className="App">
          <Navbar />
          <Routes>
            <Route path="/" exact element={<ProductsListPage />} />
            <Route path="/details" exact element={<ProductDetailsPage />} />
            <Route path="/cart" exact element={<CartPage />} />
          </Routes>
        </div>
      </ContextHook.Provider>
    </BrowserRouter>
  );
};

export default App;
