import "./productDetauls.scss";
import React, { useState, useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useQuery } from "@apollo/client";
import { productQuery } from "../../qraphQL/queries";
import ContextHook from "../../components/ContextHook";
const ProductDetailsPage = () => {
  const { id } = useParams();
  const { loading, data } = useQuery(productQuery, { variables: { id: id } });
  const [imgIndex, setImgIndex] = useState(0);
  const {
    currencyIndex,
    cartItems,
    addToCart,
    removeFromCart,
    productDetails,
    setProductDetails,
  } = useContext(ContextHook);
  const productInCart =
    !loading &&
    cartItems.find(({ id }) => (id === data.product.id ? true : false));
  useEffect(() => {
    if (!loading) {
      setProductDetails((obj) => ({ ...obj, ...data.product }));
    }
  }, [loading]);
  return (
    <div className="productDetailsPage">
      <div className="imagesChanger">
        {productDetails.gallery.map((src, idx) => (
          <img
            key={src}
            src={src}
            className={`${imgIndex === idx && "showing"}`}
            alt={src}
            onClick={() => setImgIndex(idx)}
          />
        ))}
      </div>
      <div className="image-wall">
        <img
          src={`${productDetails.gallery[imgIndex]}`}
          alt="background wall"
        />
      </div>
      <div className="aboutProduct">
        <h3 className="productCategory">{productDetails.category}</h3>
        <h2 className="productName">{productDetails.name}</h2>
        <div className="price">
          <h4>price:</h4>
          <p>
            <span>{productDetails.prices[currencyIndex].currency.symbol}</span>
            {productDetails.prices[currencyIndex].amount}
          </p>
        </div>
        <button
          onClick={() =>
            !productInCart
              ? addToCart(data, imgIndex)
              : removeFromCart(data.product)
          }
        >
          {!productInCart ? "add to cart" : "removing"}
        </button>
        <div
          className="description"
          dangerouslySetInnerHTML={{
            __html: productDetails.description,
          }}
        />
      </div>
    </div>
  );
};

export default ProductDetailsPage;
