import "./productDetauls.scss";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useQuery } from "@apollo/client";
import { productQuery } from "../../qraphQL/queries";
const ProductDetailsPage = () => {
  const { id } = useParams();
  const { loading, data } = useQuery(productQuery, { variables: { id: id } });
  const [index, setIndex] = useState(0);
  
  return (
    <div className="productDetailsPage">
      <div className="imagesChanger">
        { !loading && data.product.gallery.map((src, idx) => (
            <img
              key={src}
              src={src}
              className={index === idx && "showing"}
              alt={src}
              onClick={() => setIndex(idx)}
            />
          ))}
      </div>
      <div className="image-wall">
        <img
          src={!loading && data.product.gallery[index]}
          alt="background wall"
        />
      </div>
      <div className="about-product">
        <h3 className="productName" >{!loading && data.product.name }</h3>
         <div className="size-buttons">
            
          </div>   
      </div>

    </div>
  );
};

export default ProductDetailsPage;
