import React, { useContext } from "react";
import ContextHook from "../../components/ContextHook";
import "./cart.scss";
const CartPage = () => {
  const {
    cartItems,
    currencyIndex,
    totalSymbol,
    incrementQty,
    decrementQty,
    moveForward,
    moveBack,
    removeFromCart,
    checkOut,
    totalPrice
  } = useContext(ContextHook);
  
  return (
    <div className="cartPage">
      <h2 className="cartHeading">
        Cart
        {cartItems.length > 0 && (
          <span>
            {cartItems.length === 1 ? " 1 item" : ` ${cartItems.length} items`}{" "}
          </span>
        )}
      </h2>
      {cartItems.length === 0 ? (
        <h3 className="noItemsHeading">No Items</h3>
      ) : (
        <div className="items">
          {cartItems.map((item) => (
            <div className="item" key={item.id}>
              <div>
                <h3 className="itemCategory">{item.category}</h3>
                <h3 className="itemName">{item.name}</h3>
                <h3 className="itemPrice">
                  {item.prices[currencyIndex].currency.symbol}
                  {item.prices[currencyIndex].amount * item.quantity}
                </h3>
              </div>
              <div>
                <div className="quantity">
                  <button
                    className={`${item.quantity === 8 && "disActive"}`}
                    disabled={item.quantity === 8}
                    onClick={() => incrementQty(item)}
                  >
                    +
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    disabled={item.quantity === 1}
                    className={`${item.quantity === 1 && "disActive"}`}
                    onClick={() => decrementQty(item)}
                  >
                    -
                  </button>
                </div>
                <div className="imageContainer">
                  <img src={item.gallery[item.idx]} alt="itemImage" />
                  <div className="arrowsButtons">
                    <button
                      disabled={item.idx === 0}
                      className={`${item.idx === 0 && "disActive"}`}
                      onClick={() => moveBack(item)}
                    >
                      <i className="fas fa-angle-left"></i>
                    </button>
                    <button
                      disabled={item.idx === item.gallery.length - 1}
                      className={`${
                        item.idx === item.gallery.length - 1 && "disActive"
                      }`}
                      onClick={() => moveForward(item)}
                    >
                      <i className="fas fa-angle-right"></i>
                    </button>
                  </div>
                </div>
              </div>
              <button
                className="removeButton"
                onClick={() => removeFromCart(item)}
              >
                <i class="fas fa-trash-alt"></i>
              </button>
            </div>
          ))}
        </div>
      )}
      <div className="totalPrice">
        <p>
          Total
          <span>
            {totalSymbol}
            {totalPrice}
          </span>
        </p>
      </div>
      <button
        className={` checkOutButton ${cartItems.length === 0 && "disabled"}`}
        onClick={checkOut}
      >
        Check Out
      </button>
    </div>
  );
};
export default CartPage;
