import React, { useContext } from "react";
import { Link } from "react-router-dom";
import "./bagItems.scss";
import ContextHook from "../ContextHook";
const BagItems = () => {
  const { cartItems, useToggle, currencyIndex } = useContext(ContextHook);
  const [bagIsActive, setBagIsActive] = useToggle(false);
  const totalPrice = cartItems.reduce((a, b) => a + b, 0);
  return (
    <div className="bagItems">
      <button className="bagToggler" onClick={setBagIsActive}>
        <i className="fal fa-shopping-cart"></i>
        {cartItems.length > 0 && (
          <span className="length">{cartItems.length}</span>
        )}
      </button>
      <div className={`listItems ${bagIsActive && "active"}`}>
        <h4>
          my bag,
          {cartItems.length > 0 && <span>{cartItems.length} items</span>}
        </h4>
        {cartItems.length === 0 ? (
          <h3 className="noItemsHeading">No Items</h3>
        ) : (
          <div className="items">
            {cartItems.map((item) => (
              <div className="item">
                <div className="details">
                  <h4>item name</h4>
                  <h4>item price</h4>
                </div>
                <div className="quantity">
                  <button>+</button>
                  <span>1</span>
                  <button>-</button>
                </div>
                <img src= {`${item.prices[currencyIndex].am}`} alt="cat" />  
              </div>
            ))}
          </div>
        )}
        {/* <div className="totalPrice">
          <span>Total</span>
          <span>${totalPrice}</span>
        </div> */}
        <div className="linkAndButton">
          <Link to="/cart" onClick={setBagIsActive}>
            view bag
          </Link>
          <button className={`${cartItems.length === 0 && "disActive"}`}>
            check out
          </button>
        </div>
      </div>
    </div>
  );
};
export default BagItems;
