import "./productDetauls.scss";
import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useQuery } from "@apollo/client";
import { productQuery } from "../../qraphQL/queries";
const ProductDetailsPage = () => {
  const { id } = useParams();
  const { loading, data } = useQuery(productQuery, { variables: { id: id } });
  const [imgSrc,setImgSrc] = useState( !loading && data.product.gallery[0] );
  
  if (!loading) {
    return (
      <div className="productDetailsPage">
        <div className="imagesChanger">
          {data.product.gallery.map((src) => (
            <img key={src} src={src} alt={src} />
          ))}
        </div>
        <div className="image-wall">
          <img src={data} alt="" />  
        </div>
      </div>
    );
  }
};

export default ProductDetailsPage;
