import {
    ApolloClient,
    InMemoryCache,
    gql,
    useQuery,
  } from "@apollo/client";
  
 export  const client = new ApolloClient({
    uri: "http://localhost:4000/",
    cache: new InMemoryCache(),
  });
  
  export const SET_CATEGORIES = gql`
  query { 
	categories {
    name
  }
} `;
