import "./navbar.scss";
import React, { useEffect, useState, useContext } from "react";
import ContextHook from "../ContextHook";
import FilterButtons from "../filterButtons/FilterButtons";
import { Link } from "react-router-dom";
import { currenciesQuery } from "../../qraphQL/queries";
import { useQuery } from "@apollo/client";

const Navbar = () => {
  const { data, loading } = useQuery(currenciesQuery);
  const { listIsActive, setListIsActive, setCurrencyValue } =
    useContext(ContextHook);
  const chooseCurrency = (parameter) => {
    setListIsActive();
    setCurrencyValue(parameter);
  };
  return (
    <div className="navbar-container">
      <div className="first">
        <FilterButtons />
      </div>
      <div className="last">
        <div className="currencies">
          <button className="btnToggler" onClick={() => setListIsActive()}>
            {currencyValue}
            <i
              className={`fas fa-angle-down ${listIsActive && "rotating"} `}
            ></i>
          </button>
          <div className={`currencyChangers ${listIsActive && "show"} `}>
            {!loading &&
              data.currencies.map(({ symbol, label }) => (
                <button key={symbol} onClick={() => chooseCurrency(symbol)}>
                  {symbol} {label}
                </button>
              ))}
          </div>
        </div>
        <Link to="/cart">
          <i className="fal fa-shopping-cart"></i>
        </Link>
      </div>
    </div>
  );
};
export default Navbar;
