import React, { useContext } from "react";
import ContextHook from "../../components/ContextHook";
import "./cart.scss";
const CartPage = () => {
  const { cartItems,currencyIndex } = useContext(ContextHook);
  return (
    <div className="cartPage">
      <h2 className="cartHeading">Cart</h2>
      {cartItems.length === 0 ? (
        <h3 className = "noItemsHeading" >No Items</h3>
      ) : (
        <div className="items">
          {cartItems.map((item) => (
            <div className="item" key={item.id}>
              <div>
                 <h3 classname = "itemName" >{item.name}</h3>
                 <h3 classname = "itemPrice" > {item.prices[currencyIndex].amount} {item.prices[currencyIndex].amount}</h3>
              </div>
              <div>b</div>  
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CartPage;
