import React, { useContext, useState } from "react";
import ContextHook from "../../components/ContextHook";
import "./cart.scss";
const CartPage = () => {
  const { cartItems,  currencyIndex } = useContext(ContextHook);
  // const moveForward = (product) => {
  //   product.idx !== product.gallery.length - 1 &&
  //     setCartItems((cart) =>
  //       cart.map((item) =>
  //         item.id === product.id ? { ...product, idx: product.idx + 1 } : item
  //       )
  //     );
  // };

  // const moveBack = (product) => {
  //   product.idx !== 0 &&
  //     setCartItems((cart) =>
  //       cart.map((item) =>
  //         item.id === product.id ? { ...product, idx: product.idx - 1 } : item
  //       )
  //     );
  // };
  // const removeItem = (product) => {
  //   setCartItems((cart) => cart.filter(({ id }) => id !== product.id));
  // };

  return (
    <div className="cartPage">
      <h2 className="cartHeading">Cart</h2>
      {cartItems.length === 0 ? (
        <h3 className="noItemsHeading">No Items</h3>
      ) : (
        <div className="items">
          {cartItems.map((item) => (
            <div className="item" key={item.id}>
              <div>
                <h3 className="itemCategory">{item.category}</h3>
                <h3 className="itemName">{item.name}</h3>
                <h3 className="itemPrice">
                  {item.prices[currencyIndex].currency.symbol}
                  {item.prices[currencyIndex].amount}
                </h3>
              </div>
              <div>
                <div className="quantity">
                  <button>+</button>
                  <span>{item.quantity}</span>
                  <button>-</button>
                </div>
                <div className="imageContainer">
                  <img src={item.gallery[item.idx]} alt="itemImage" />
                  <div className="arrowsButtons">
                    <button
                      className={`${item.idx === 0 && "disabled"}`}
                      onClick={() => moveBack(item)}
                    >
                      <i className="fas fa-angle-left"></i>
                    </button>
                    <button
                      className={`${
                        item.idx === item.gallery.length - 1 && "disabled"
                      }`}
                      onClick={() => moveForward(item)}
                    >
                      <i className="fas fa-angle-right"></i>
                    </button>
                  </div>
                </div>
              </div>
              <button className="removeButton" onClick={() => removeItem(item)}>
                <i class="fas fa-trash-alt"></i>
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CartPage;
