import React, { useContext } from "react";
import { Link } from "react-router-dom";
import "./bagItems.scss";
import ContextHook from "../ContextHook";
const BagItems = () => {
  const { cartItems, setCartItems,  currencyIndex, totalSymbol } =
    useContext(ContextHook);
  const [bagIsActive, setBagIsActive] = useToggle(false);
  const totalPrice = cartItems.reduce(
    (price, item) => price + item.quantity * item.prices[currencyIndex].amount,
    0
  );
  const increment = (product) => {
    setCartItems((cart) =>
      cart.map((item) =>
        item.id === product.id
          ? { ...product, quantity: product.quantity + 1 }
          : item
      )
    );
  };
  const decrement = (product) => {
    setCartItems((cart) =>
      cart.map((item) =>
        item.id === product.id && product.quantity > 1
          ? { ...product, quantity: product.quantity - 1 }
          : item
      )
    );
  };
  const checkOut = () => {
    alert("Purchased successfully");
    window.location.href = "/";
  };
  return (
    <div className="bagItems">
      <button className="bagToggler" onClick={setBagIsActive}>
        <i className="fal fa-shopping-cart"></i>
        {cartItems.length > 0 && (
          <span className="length">{cartItems.length}</span>
        )}
      </button>
      <div className={`listItems ${bagIsActive && "active"}`}>
        <h4>
          my bag,
          {cartItems.length > 0 && <span>{cartItems.length} items</span>}
        </h4>
        {cartItems.length === 0 ? (
          <h3 className="noItemsHeading">No Items</h3>
        ) : (
          <div className="items">
            {cartItems.map((item) => (
              <div className="item">
                <div className="details">
                  <h4>{item.name}</h4>
                  <h4>
                    {item.prices[currencyIndex].currency.symbol}
                    {item.prices[currencyIndex].amount * item.quantity}
                  </h4>
                </div>
                <div className="quantity">
                  <button onClick={() => increment(item)}>+</button>
                  <span>{item.quantity}</span>
                  <button onClick={() => decrement(item)}>-</button>
                </div>
                <img src={`${item.selectedImg}`} alt="cat" />
              </div>
            ))}
          </div>
        )}
        <div className="totalPrice">
          <span>Total</span>
          <span>
            {totalSymbol} {totalPrice}
          </span>
        </div>
        <div className="linkAndButton">
          <Link to="/cart" onClick={setBagIsActive}>
            view bag
          </Link>
          <button
            className={`${cartItems.length === 0 && "disActive"}`}
            onClick={checkOut}
          >
            check out
          </button>
        </div>
      </div>
    </div>
  );
};
export default BagItems;
