import React, { useContext } from "react";
import { useQuery } from "@apollo/client";
import ContextHook from "./ContextHook";
const Buttons = () => {
  const { categories } = useContext(ContextHook);
  return (
    <>
      {categories.loading ? (
        <h3>Loading...</h3>
      ) : (
        categories.data.categories.map((name) => <button key={name}>{name}</button>)
      )}
    </>
  );
};

export default Buttons;
