import React, { useContext } from "react";
import ContextHook from "../../components/ContextHook";
import "./productsLists.scss";
import { useQuery } from "@apollo/client";
import { categoriesQuery } from "../../qraphQL/ApolloClient";
import Products from "../../components/products/Products";
const ProductsListPage = () => {
  const { categorySelected } = useContext(ContextHook);
  const { loading, data } = useQuery(categoriesQuery);
  return (
    <div className="productsListsPage">
      {loading ? (
        <h3>Loading...</h3>
      ) : categorySelected === "all" ? (
        data.categories.map((c) => <div className="products">{c.name}</div>)
      ) : (
        data.categories
          .filter((c) => c.name === categorySelected)
          .map((c) => <div className="products">{c.name}</div>)
      )}
    </div>
  );
};

export default ProductsListPage;
