import "./navbar.scss";
import React, { useContext, useEffect } from "react";
import ContextHook from "../ContextHook";
import CategoriesLinks from "../categoriesLinks/CategoriesLinks";
import { currenciesQuery } from "../../qraphQL/queries";
import { useQuery } from "@apollo/client";
import BagItems from "../bagItems/BagItems";
const Navbar = () => {
  const { data, loading } = useQuery(currenciesQuery);
  const [bagIsActive, setBagIsActive] = useToggle(false);
  
  const { useToggle, currencyIndex,chooseCurrency } =
    useContext(ContextHook);
  // const chooseCurrency = (param, symbol) => {
  //   setCurrencyIndex(param);
  //   setListIsActive();
  //   setTotalSymbol(symbol);
  // };
  useEffect(() => {
    window.onkeydown = (e) => {
      if (e.code === "Escape") {
        listIsActive && setListIsActive();
        bagIsActive && setBagIsActive();
      }
    };
  });
  return (
    <div className="navbar-container">
      <div className="first">
        <CategoriesLinks />
      </div>
      <div className="last">
        <div className="currencies">
          <button className="btnToggler" onClick={() => setListIsActive()}>
            {!loading && data.currencies[currencyIndex].symbol}
            <i
              className={`fas fa-angle-down ${listIsActive && "rotating"} `}
            ></i>
          </button>
          <div className={`currencyChangers ${listIsActive && "show"} `}>
            {!loading &&
              data.currencies.map(({ symbol, label }, idx) => (
                <button
                  key={symbol}
                  onClick={() => chooseCurrency(idx, symbol)}
                >
                  {`${symbol}  ${label}`}
                </button>
              ))}
          </div>
        </div>
        <BagItems bagIsActive={bagIsActive} setBagIsActive={setBagIsActive} />
      </div>
    </div>
  );
};
export default Navbar;
