import React, { useContext } from "react";
import { useQuery } from "@apollo/client";
import ContextHook from "./ContextHook";
import { categoriesQuery } from "../qraphQL/ApolloClient";
const Buttons = () => {
  // const {categoriesQuery} = useContext(ContextHook);
  const { loading, error, data } = useQuery(categoriesQuery);
  console.log(data);
  return (
    <>
      {loading ? (
        <h3>Loading...</h3>
      ) : (
        data.categories.map(({ name }) => <button key={name}>{name}</button>)
      )}
    </>
  );
};

export default Buttons;
