import "./navbar.scss";
import React, { useState } from "react";
import FilterButtons from "../filterButtons/FilterButtons";
import { Link } from "react-router-dom";
import { currenciesQuery } from "../../qraphQL/queries";
import { useQuery } from "@apollo/client";
import { useToggle } from "../customHooks";

const Navbar = () => {
  const { data, loading } = useQuery(currenciesQuery);
  const [currencyValue, setCurrencyValue] = useState("$");
  const [showed, setShowed] = useToggle(false);
  return (
    <div className="navbar-container">
      <div className="first">
        <FilterButtons />
      </div>
      <div className="last">
        <div className="currencies">
          <button className="btnToggler" onClick={() => setShowed()}>
            {currencyValue}
            <i class="fas fa-angle-down"></i>
          </button>
          <div className={`currencyChangers`}  >
            {!loading &&
              data.currencies.map(({ symbol, label }) => (
                <button key={symbol}>
                  {symbol} {label}
                </button>
              ))}
          </div>
        </div>
        <Link to="/cart">
          <i className="fal fa-shopping-cart"></i>
        </Link>
      </div>
    </div>
  );
};
export default Navbar;
