import React, { useContext } from "react";
import { Link } from "react-router-dom";
import "./bagItems.scss";
import ContextHook from "../ContextHook";
const BagItems = () => {
  const {
    cartItems,
    currencyIndex,
    totalSymbol,
    incrementQty,
    decrementQty,
    checkOut,
    bagIsActive,
    setBagIsActive, 
    listIsActive,
    setListIsActive,
    moveBack,
    moveForward,
    removeFromCart,
  } = useContext(ContextHook);
  const totalPrice = cartItems.reduce(
    (price, item) => price + item.quantity * item.prices[currencyIndex].amount,
    0
  );
  const toggle = () => {
    setBagIsActive();
    listIsActive && setListIsActive();
  };
  const routeToCart = () => { 
    window.scrollTo(0,0);
  } 

  return (
    <div className="bagItems">
      <button className="bagToggler" onClick={toggle}>
        <i className="fal fa-shopping-cart"></i>
        {cartItems.length > 0 && (
          <span className="length">{cartItems.length}</span>
        )}
      </button>
      <div className={`listItems ${bagIsActive && "active"}`}>
        <h4 className="bagHeading" >
          my bag
          {cartItems.length > 0 && (
            <span>
              {cartItems.length === 1
                ? " 1 item"
                : ` ${cartItems.length} items`}
            </span>
          )}
        </h4>
        {cartItems.length === 0 ? (
          <h3 className="noItemsHeading">No Items</h3>
        ) : (
          <div className="items">
            {cartItems.map((item) => (
              <div className="item" key={item.id}>
                <div className="details">
                  <h4>{item.name}</h4>
                  <h4>
                    {item.prices[currencyIndex].currency.symbol}
                    {item.prices[currencyIndex].amount * item.quantity}
                  </h4>
                </div>
                <div className="quantity">
                  <button
                    disabled={item.quantity === 8}
                    className={`${item.quantity === 8 && "disActive"}`}
                    onClick={() => incrementQty(item)}
                  >
                    +
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    disabled={item.quantity === 1}
                    className={`${item.quantity === 1 && "disActive"}`}
                    onClick={() => decrementQty(item)}
                  >
                    -
                  </button>
                </div>
                <div className="imageContainer">
                  <img src={`${item.gallery[item.idx]}`} alt="cat" />
                  <div className="arrowsButtons">
                    <button
                      disabled={item.idx === 0}
                      className={`${item.idx === 0 && "disActive"}`}
                      onClick={() => moveBack(item)}
                    >
                      <i className="fas fa-angle-left"></i>
                    </button>
                    <button
                      disabled={item.idx === item.gallery.length - 1}
                      className={`${
                        item.idx === item.gallery.length - 1 && "disActive"
                      }`}
                      onClick={() => moveForward(item)}
                    >
                      <i className="fas fa-angle-right"></i>
                    </button>
                  </div>
                </div>
                <button
                  className="remvoveItem"
                  onClick={() => removeFromCart(item)}
                >
                  remove
                </button>
              </div>
            ))}
          </div>
        )}
        <div className="totalPrice">
          <span>Total</span>
          <span>
            {totalSymbol}
            {totalPrice}
          </span>
        </div>
        <div className="linkAndButton">
          <Link to="/cart" onClick={setBagIsActive}>
            view bag
          </Link>
          <button
            className={`${cartItems.length === 0 && "disActive"}`}
            onClick={checkOut}
          >
            check out
          </button>
        </div>
      </div>
    </div>
  );
};
export default BagItems;
